import styles from './Header.module.css';
import 'react-date-range/dist/styles.css';
import 'react-date-range/dist/theme/default.css';
import useComponentVisible from '../../custom-hooks/useComponentVisible';
import DatePicker from './usefull_components/DatePicker';

import { useState } from 'react';
import { Link, Navigate, useNavigate } from 'react-router-dom';

const Header = () => {
  const [searchDate, setSearchDate] = useState([
    {
      startDate: new Date(),
      endDate: null,
      key: 'selection',
    },
  ]);

  const [searchQty, setSearchQty] = useState(null);
  const navigate = useNavigate();
  const [searchLocation, setSearchLocation] = useState('');
  const inputNumberChangeHandler = () => {
    setQtyVisible(true);
    return;
  };
  const SearchHandler = () => {
    navigate('/search', {
      state: {
        location: searchLocation,
        dateRange: searchDate,
        quantity: searchQty,
      },
    });

    return;
  };

  const qtyInputSubmit = (e) => {
    e.preventDefault();
    const quantity = {
      adult: e.target.adult.value,
      children: e.target.children.value,
      room: e.target.room.value,
    };
    setSearchQty(quantity);
    setQtyVisible(false);
    return;
  };
  const searchLocationChangeHandler = (e) => {
    e.target.value && setSearchLocation(e.target.value);
  };
  return (
    <div className={styles.header}>
      <h3>A lifetime of discounts? It Genius.</h3>
      <p>
        Get rewarded for your travels - unlock instan savings of 10% or more
        with a free acount
      </p>
      <button>Sign in/ Register</button>

      <div className={styles.wrapper}>
        {qtyVisible && (
          <div ref={qtyRef} className={styles['quantity-input']}>
            <form onSubmit={qtyInputSubmit}>
              <div>
                <label htmlFor='adult'>Adult</label>
                <input
                  id='adult'
                  name='adult'
                  type='number'
                  defaultValue={searchQty ? searchQty.adult : 0}
                  min={0}
                ></input>
              </div>
              <div>
                <label htmlFor='children'>Children</label>
                <input
                  id='children'
                  name='children'
                  type='number'
                  min={0}
                  defaultValue={searchQty ? searchQty.children : 0}
                ></input>
              </div>
              <div>
                <label htmlFor='room'>Room</label>
                <input
                  id='room'
                  name='room'
                  type='number'
                  min={0}
                  defaultValue={searchQty ? searchQty.room : 0}
                ></input>
              </div>
              <button type='submit'>OK</button>
            </form>
          </div>
        )}

        <input
          onChange={searchLocationChangeHandler}
          id='searchLocation'
          name='searchLocation'
          type='text'
          placeholder='&#xf238; Where are you going?'
        />
        <DatePicker setDateData={setSearchDate} searchDateData={searchDate} />

        <input
          onClick={inputNumberChangeHandler}
          type='text'
          placeholder='&#xf183; 1 adult . 0 children .  1 room'
          defaultValue={
            searchQty
              ? ` ${searchQty.adult} adult . ${searchQty.children} children .  ${searchQty.room} room`
              : ''
          }
        ></input>
        <button onClick={SearchHandler}>Search</button>
      </div>
    </div>
  );
};
export default Header;
