import { useState, useEffect } from 'react';
import useComponentVisible from '../../custom-hooks/useComponentVisible';
import { DateRange } from 'react-date-range';
import styles from './DatePicker.module.css';
const DatePicker = (props) => {
  const {
    ref: datePickerRef,
    visible: datePickerVisible,
    setVisible: setDatePickerVisible,
  } = useComponentVisible(false);
  const [searchDate, setSearchDate] = useState([
    {
      startDate: new Date(),
      endDate: null,
      key: 'selection',
    },
  ]);
  const dateSelectedHandler = (e) => {
    setDatePickerVisible(true);
    return;
  };
  useEffect(() => {
    console.log('use effect', searchDate);
    props.setDateData(searchDate);
  }, []);
  return (
    <>
      <input
        type='text'
        defaultValue={
          searchDate[0].endDate != null
            ? `${searchDate[0].startDate.toLocaleDateString(
                'en-us'
              )} to ${searchDate[0].endDate.toLocaleDateString('en-us')}`
            : ''
        }
        onClick={dateSelectedHandler}
        placeholder='&#xf073; 06/24/22 to 06/24/22'
      ></input>
      {datePickerVisible && (
        <div ref={datePickerRef} className={styles['date-picker']}>
          <DateRange
            editableDateInputs={true}
            onChange={(item) => setSearchDate([item.selection])}
            moveRangeOnFirstSelection={false}
            ranges={searchDate}
          />
        </div>
      )}
    </>
  );
};
export default DatePicker;
