import styles from './Header.module.css';
import 'react-date-range/dist/styles.css';
import 'react-date-range/dist/theme/default.css';

import DatePicker from './DatePicker';
import { DateRange } from 'react-date-range';
import { useState } from 'react';
import useComponentVisible from './DatePicker';
const Header = () => {
  const [datePickerState, setDatePickerState] = useState([
    {
      startDate: new Date(),
      endDate: null,
      key: 'selection',
    },
  ]);
  const [dateRange, setDateRange] = useState(null);
  const {
    ref: datePickerRef,
    visible: datePickerVisible,
    setVisible: setDatePickerVisible,
  } = useComponentVisible(false);
  const {
    ref: qtyRef,
    visible: qtyVisible,
    setVisible: setQtyVisible,
  } = useComponentVisible(false);
  const [quantity, setQuantity] = useState(null);
  const inputNumberChangeHandler = () => {
    setQtyVisible(true);
    return;
  };
  const SearchHandler = () => {
    window.location.href = '../search';
    return;
  };
  const dateSelectedHandler = (e) => {
    setDatePickerVisible(true);

    return;
  };
  const dateSelectChangeHandler = (e) => {
    setDateRange(datePickerState);
    console.log(dateRange);
  };
  const qtyInputSubmit = (e) => {
    e.preventDefault();
    const quantity = {
      adult: e.target.adult.value,
      children: e.target.children.value,
      room: e.target.room.value,
    };
    setQuantity(quantity);
    setQtyVisible(false);
    return;
  };
  return (
    <div className={styles.header}>
      <h3>A lifetime of discounts? It Genius.</h3>
      <p>
        Get rewarded for your travels - unlock instan savings of 10% or more
        with a free acount
      </p>
      <button>Sign in/ Register</button>

      <div className={styles.wrapper}>
        {qtyVisible && (
          <div ref={qtyRef} className={styles['quantity-input']}>
            <form onSubmit={qtyInputSubmit}>
              <div>
                <label htmlFor='adult'>Adult</label>
                <input
                  id='adult'
                  name='adult'
                  type='number'
                  defaultValue={0}
                  value={quantity.adult}
                  min={0}
                ></input>
              </div>
              <div>
                <label htmlFor='children'>Children</label>
                <input
                  id='children'
                  name='children'
                  type='number'
                  min={0}
                  defaultValue={0}
                  value={quantity.children}
                ></input>
              </div>
              <div>
                <label htmlFor='room'>Room</label>
                <input
                  id='room'
                  name='room'
                  type='number'
                  min={0}
                  defaultValue={0}
                  value={quantity.room}
                ></input>
              </div>
              <button type='submit'>OK</button>
            </form>
          </div>
        )}
        {datePickerVisible && (
          <div ref={datePickerRef} className={styles['date-picker']}>
            <DateRange
              editableDateInputs={true}
              onChange={(item) => setDatePickerState([item.selection])}
              moveRangeOnFirstSelection={false}
              ranges={datePickerState}
            />
          </div>
        )}
        <input type='text' placeholder='&#xf238; Where are you going?' />
        <input
          type='text'
          value={
            datePickerState[0].endDate != null
              ? `${datePickerState[0].startDate.toLocaleDateString(
                  'en-us'
                )} to ${datePickerState[0].endDate.toLocaleDateString('en-us')}`
              : ''
          }
          onClick={dateSelectedHandler}
          onInput={dateSelectChangeHandler}
          placeholder='&#xf073; 06/24/22 to 06/24/22'
        ></input>

        <input
          onClick={inputNumberChangeHandler}
          type='text'
          placeholder='&#xf183; 1 adult . 0 children .  1 room'
          value={`&#xf183; ${quantity.adult} adult . ${quantity.children} children .  ${quantity.room} room`}
        ></input>
        <button onClick={SearchHandler}>Search</button>
      </div>
    </div>
  );
};
export default Header;
