import Redux, { useSelector, useDispatch } from 'react-redux';
import { legacy_createStore as createStore } from 'redux';
import classes from './Counter.module.css';

const Counter = () => {
  const state = { counter: 0 };
  const INCREMENT = 'INCREMENT';
  const DECREMENT = 'DECREMENT';
  const dispatch = useDispatch();
  const counterProducer = (state, action) => {
    switch (action.type) {
      case INCREMENT:
        return Object.assign({}, { counter: state.counter + 1 });
      case DECREMENT:
        return Object.assign({}, { counter: state.counter - 1 });
      default:
        return state;
    }
  };
  const incrementCounter = () => {
    return { type: 'INCREMENT' };
  };
  const decrementCounter = () => {
    return { type: 'DECREMENT' };
  };
  const store = createStore(counterProducer);
  const toggleCounterHandler = () => {};
  const incrementCounterHandler = () => {
    store.dispatch(incrementCounter());
  };
  const decrementCounterHandler = () => {};
  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{state.counter}</div>
      <div>
        <button onClick={incrementCounterHandler}>Increment</button>
        <button onClick={decrementCounterHandler}>Decrement</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
